  entry pot(xyz,v)
      x(1)=sqrt((xyz(1,1)-xyz(2,1))**2+(xyz(1,2)-xyz(2,2))**2+(xyz(1,3)-xyz(2,3))**2)
      x(2)=sqrt((xyz(1,1)-xyz(3,1))**2+(xyz(1,2)-xyz(3,2))**2+(xyz(1,3)-xyz(3,3))**2)
      x(3)=sqrt((xyz(1,1)-xyz(4,1))**2+(xyz(1,2)-xyz(4,2))**2+(xyz(1,3)-xyz(4,3))**2)
      x(4)=sqrt((xyz(2,1)-xyz(3,1))**2+(xyz(2,2)-xyz(3,2))**2+(xyz(2,3)-xyz(3,3))**2)
      x(5)=sqrt((xyz(2,1)-xyz(4,1))**2+(xyz(2,2)-xyz(4,2))**2+(xyz(2,3)-xyz(4,3))**2)
      x(6)=sqrt((xyz(3,1)-xyz(4,1))**2+(xyz(3,2)-xyz(4,2))**2+(xyz(3,3)-xyz(4,3))**2)
  
        x(1)=x(1)/0.5291772083_dp
        x(1)=exp(-x(1)/a1)
  
        x(2)=x(2)/0.5291772083_dp
        x(2)=exp(-x(2)/a2)
  
        x(3)=x(3)/0.5291772083_dp
        x(3)=exp(-x(3)/a3)
  
        x(4)=x(4)/0.5291772083_dp
        x(4)=exp(-x(4)/a2)
  
        x(5)=x(5)/0.5291772083_dp
        x(5)=exp(-x(5)/a3)
  
        x(6)=x(6)/0.5291772083_dp
        x(6)=exp(-x(6)/a4)
  
      v=emsav(x,coeff)
  end subroutine 
  subroutine potder(xyz,dxyz)
  implicit none
  integer, parameter:: dp=kind(0.d0)                   ! double precision
  real(dp), intent(in) :: xyz(4,3)
  real(dp), intent(out) :: dxyz(4,3)
  real(dp) :: h, v1, v2
  real(dp) :: xyz_t(4,3)
  integer :: i, j
  h=0.01_dp*0.5291772_dp
  
  do i=1,4
    do j=1,3
      xyz_t=xyz
      xyz_t(i,j)=xyz(i,j)-h
      call pot(xyz_t,v1)
      xyz_t(i,j)=xyz(i,j)+h
      call pot(xyz_t,v2)
      dxyz(i,j)=(-0.5_dp*v1+0.5_dp*v2)/h
    end do
  end do
  end subroutine
  real*8 function emsav(x,c) result(v)
    implicit none
    integer, parameter :: wp  = kind(1.0D0)
    real(wp),dimension(1:6)::x
    real(wp),dimension(0:501)::c
  !  real(wp)::v
    ! ::::::::::::::::::::
    real(wp),dimension(0:501)::p
  
    call bemsav(x,p)
    v = dot_product(p,c)
  
    return
  end function emsav
  
  subroutine bemsav(x,p)
    implicit none
    integer, parameter :: wp  = kind(1.0D0)
    real(wp),dimension(1:6),intent(in)::x
    real(wp),dimension(0:501),intent(out)::p
    ! ::::::::::::::::::::
    real(wp),dimension(0:10)::m
  
    call evmono(x,m)
    call evpoly(m,p)
  
    return
  end subroutine bemsav
  subroutine evmono(x,m)
    implicit none
    integer, parameter :: wp  = kind(1.0D0)
    real(wp),dimension(1:6),intent(in)::x
    real(wp),dimension(0:10),intent(out)::m
    !::::::::::::::::::::
  
    m(0) = 1.0D0
    m(1) = x(6)
    m(2) = x(5)
    m(3) = x(3)
    m(4) = x(4)
    m(5) = x(2)
    m(6) = x(1)
    m(7) = m(2)*m(3)
    m(8) = m(3)*m(4)
    m(9) = m(2)*m(5)
    m(10) = m(4)*m(5)
  
    return
  end subroutine evmono
