class Lingkaran:
    def __init__(self, radius):
        self.radius = radius  # Variabel objek
    
    def hitung_luas(self):
        return 3.14 * self.radius * self.radius
    
    def hitung_keliling(self):
        return 2 * 3.14 * self.radius 
    
    def menghitung_luas():
        return "ini fungsi menghitung luas"